  public void waitUntilReady() {
    handler = new Handler( getLooper() );
  }

  // Add to message queue
  public void dispatchAsync( Runnable task ) {
    handler.post( task );
  }

  public void removePendingJobs() {
    handler.removeCallbacksAndMessages( null );
  }
}

...

