  // Test cases
  this.xhr = new XMLHttpRequest();
  this.xhr.addEventListener( 
    'load', 
    ( evt ) => this.doAnnotationsLoaded( evt ) 
  );
  this.xhr.open( 
    'GET', 
    'dataset/annotations.txt', 
    true 
  );
  this.xhr.send( null );
}

...

